import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; // Estilos del calendario
import es from "date-fns/locale/es"; // Localización en español

registerLocale("es", es); // Registrar idioma español

const RoomDetails = () => {
    const { id } = useParams(); // Obtén el ID de la habitación desde la URL
    const navigate = useNavigate(); // Para navegar de regreso
    const room = rooms.find((room) => room.id === parseInt(id)); // Busca la habitación por ID

    // Estado para el selector de personas (1 o 2 personas)
    const [selectedOption, setSelectedOption] = useState(1);

    // Estado para fechas de ingreso y salida
    const [checkIn, setCheckIn] = useState(null);
    const [checkOut, setCheckOut] = useState(null);

    // Estado para la disponibilidad
    const [isAvailable, setIsAvailable] = useState(false);

    // Verificar disponibilidad cuando se seleccionan ambas fechas
    useEffect(() => {
        if (checkIn && checkOut) {
            // Aquí puedes conectar con el backend para verificar disponibilidad
            const isRoomAvailable = Math.random() > 0.5; // Simulación de disponibilidad
            setIsAvailable(isRoomAvailable);
        }
    }, [checkIn, checkOut]);

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            {/* Imagen de la habitación */}
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: '100%',
                    maxWidth: '600px',
                    borderRadius: '8px',
                    marginBottom: '20px',
                }}
            />

            {/* Información de la habitación */}
            <h1>{room.type}</h1>
            <p>
                Precio:{" "}
                <strong>
                    {selectedOption === 1
                        ? `${room.priceForOne} CLP`
                        : `${room.priceForTwo || room.priceForOne} CLP`}
                </strong>
            </p>
            <label>
                Personas:{" "}
                <select
                    value={selectedOption}
                    onChange={(e) => setSelectedOption(parseInt(e.target.value))}
                    style={{
                        marginLeft: '10px',
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                    }}
                >
                    <option value={1}>1 Persona</option>
                    {room.priceForTwo && <option value={2}>2 Personas</option>}
                </select>
            </label>

            {/* Fechas de ingreso y salida */}
            <div style={{ marginTop: '20px' }}>
                <h2>Selecciona tus fechas</h2>

                {/* Fecha de ingreso */}
                <div style={{ marginBottom: '10px' }}>
                    <p>Fecha de ingreso:</p>
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => setCheckIn(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de ingreso"
                        minDate={new Date()}
                    />
                </div>

                {/* Fecha de salida */}
                <div style={{ marginBottom: '10px' }}>
                    <p>Fecha de salida:</p>
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => setCheckOut(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de salida"
                        minDate={checkIn || new Date()}
                    />
                </div>

                {/* Mensaje de disponibilidad */}
                {checkIn && checkOut && (
                    <p style={{ color: isAvailable ? "green" : "red", fontWeight: "bold" }}>
                        {isAvailable ? "¡Disponible!" : "No disponible"}
                    </p>
                )}
            </div>

            {/* Detalles adicionales */}
            <ul style={{ textAlign: 'left', margin: '20px auto', maxWidth: '600px' }}>
                <li>
                    <strong>Disponibilidad:</strong>{" "}
                    <span
                        style={{
                            color: room.available ? "#28a745" : "#dc3545",
                            fontWeight: "bold",
                        }}
                    >
                        {room.available ? "Disponible" : "No disponible"}
                    </span>
                </li>
                <li><strong>Comodidades:</strong> Ejemplo: Wifi, Baño Privado, Aire Acondicionado</li>
            </ul>

            {/* Botones de acción */}
            <div style={{ marginTop: '20px' }}>
                {/* Botón Volver */}
                <button
                    onClick={() => navigate(-1)}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginRight: '10px',
                    }}
                >
                    Volver
                </button>

                {/* Botón Reservar */}
                <button
                    onClick={() => alert('Reservar lógica aquí')}
                    disabled={!isAvailable}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: isAvailable ? '#28a745' : '#ddd',
                        color: isAvailable ? '#fff' : '#999',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: isAvailable ? 'pointer' : 'not-allowed',
                        fontSize: '16px',
                    }}
                >
                    Reservar
                </button>
            </div>
        </div>
    );
};

export default RoomDetails;
