import React, { useState, useEffect } from 'react';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const [checkIn, setCheckIn] = useState(null);
    const [checkOut, setCheckOut] = useState(null);
    const [availability, setAvailability] = useState({});

    // Ordenar las habitaciones por ID
    const sortedRooms = roomsData.sort((a, b) => a.id - b.id);

    const fetchAvailability = async () => {
        if (!checkIn || !checkOut) return;
        const formattedCheckIn = checkIn.toISOString().split("T")[0];
        const formattedCheckOut = checkOut.toISOString().split("T")[0];

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ checkIn: formattedCheckIn, checkOut: formattedCheckOut }),
            });

            const data = await response.json();
            const updatedAvailability = sortedRooms.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id);
                return acc;
            }, {});

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    useEffect(() => {
        if (checkIn && checkOut) {
            fetchAvailability();
        }
    }, [checkIn, checkOut]);

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal</h1>

            <div className="calendar-container">
                <label>Selecciona tu rango de fechas:</label>
                <div>
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => {
                            setCheckIn(date);
                            if (checkOut && date >= checkOut) setCheckOut(null);
                        }}
                        locale="es"
                        placeholderText="Fecha de ingreso"
                        minDate={new Date()}
                        dateFormat="dd/MM/yyyy"
                    />
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => setCheckOut(date)}
                        locale="es"
                        placeholderText="Fecha de salida"
                        minDate={checkIn ? new Date(checkIn.getTime() + 24 * 60 * 60 * 1000) : new Date()}
                        dateFormat="dd/MM/yyyy"
                    />
                </div>
            </div>

            {/* Pasar las habitaciones ordenadas */}
            <RoomList rooms={sortedRooms} availability={availability} />
        </div>
    );
};

export default Home;
