import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';

registerLocale("es", es);

const RoomList = ({ rooms = [], availability: initialAvailability = {} }) => {
    // Estado para la fecha seleccionada en el calendario
    const [selectedDate, setSelectedDate] = useState(null);

    // Estado para la disponibilidad actualizada
    const [availability, setAvailability] = useState(
        rooms.reduce((acc, room) => {
            acc[room.id] = initialAvailability[room.id] || room.available || false;
            return acc;
        }, {})
    );

    // Estado para controlar la selección de personas (1 o 2 personas)
    const [selectedOptions, setSelectedOptions] = useState(
        rooms.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    // Función para verificar disponibilidad en el backend
    const fetchAvailability = async (date) => {
        if (!date) return; // No realizar consulta si no hay fecha seleccionada
        const formattedDate = date.toISOString().split("T")[0];
        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedDate,
                    checkOut: formattedDate,
                }),
            });

            const data = await response.json();
            const updatedAvailability = rooms.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id);
                return acc;
            }, {});
            setAvailability(updatedAvailability); // Actualizar disponibilidad
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Llamar a fetchAvailability cuando cambia la fecha seleccionada
    useEffect(() => {
        if (selectedDate) {
            fetchAvailability(selectedDate);
        }
    }, [selectedDate]);

    // Manejar cambio en el selector de personas
    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    // Depuración: Verificar disponibilidad en consola
    console.log("Disponibilidad recibida en RoomList:", availability);

    return (
        <div className="room-list-container">
            {/* Calendario */}
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <div className="room-list">
                {rooms.map((room) => (
                    <div key={room.id} className="room-card">
                        {/* Imagen de la habitación */}
                        <img src={room.image} alt={room.type} className="room-image" />

                        {/* ID de la habitación */}
                        <p className="room-id">
                            ID: <strong>{room.id}</strong>
                        </p>

                        {/* Nombre de la habitación */}
                        <h2>{room.type}</h2>

                        {/* Precio dinámico */}
                        <p>
                            Precio:{" "}
                            <strong>
                                {selectedOptions[room.id] === 1
                                    ? `${room.priceForOne} CLP`
                                    : `${room.priceForTwo || room.priceForOne} CLP`}
                            </strong>
                        </p>

                        {/* Selector de personas */}
                        {![4, 5, 8].includes(room.id) && (
                            <label>
                                Personas:
                                <select
                                    value={selectedOptions[room.id]}
                                    onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                                >
                                    <option value={1}>1 Persona</option>
                                    {room.priceForTwo && <option value={2}>2 Personas</option>}
                                </select>
                            </label>
                        )}

                        {/* Disponibilidad */}
                        <p className="room-availability">
                            Disponibilidad:{" "}
                            <span className={availability[room.id] === false ? "not-available" : "available"}>
                                {availability[room.id] === false ? "No disponible" : "Disponible"}
                            </span>
                        </p>

                        {/* Botón Ver Detalles (deshabilitado si no disponible) */}
                        <Link
                            to={`/room/${room.id}`}
                            className={`details-btn ${availability[room.id] === false ? "disabled" : ""}`}
                            onClick={(e) => availability[room.id] === false && e.preventDefault()}
                        >
                            {availability[room.id] === false ? "No Disponible" : "Ver Detalles"}
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default RoomList;
